sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetFuelTypesFromAircraft(vAircraft, vExpected)
	{
		function TestCase_GetFuelTypesFromAircraft_CheckResult(vInput,vExpected,vResult)
		{
			return x_snc_devtools.IsEqual(vExpected,vResult);
		}

		test.TestCase(
			'Function GetFuelTypesFromAircraft()',
			GetFuelTypesFromAircraft,
			{
				'Aircraft': vAircraft
			},
			vExpected,
			TestCase_GetFuelTypesFromAircraft_CheckResult);
	}

	TestCase_GetFuelTypesFromAircraft(null,[]);

	var grFuelType = new GlideRecord('x_snc_airline_fuel_type');
	grFuelType.initialize();
	grFuelType.name = 'ATF_FUEL_TYPE_1';
	var strFuelType1SysId = grFuelType.insert();

	var grFuelType = new GlideRecord('x_snc_airline_fuel_type');
	grFuelType.initialize();
	grFuelType.name = 'ATF_FUEL_TYPE_2';
	var strFuelType2SysId = grFuelType.insert();


	var grModel = new GlideRecord('x_snc_airline_aircraft_model');
	grModel.initialize();
	grModel.manufacturer = 'ATF_MANUFACTURER_1';
	grModel.model = 'ATF_MODEL_1';
	grModel.type = 'ATF_TYPE_1';
	//grModel.fuel_type = strFuelType1SysId;
	var strModel1SysId = grModel.insert();

	var grAircraft = new GlideRecord('x_snc_airline_aircraft');
	grAircraft.initialize();
	grAircraft.model = strModel1SysId;
	var strAircraft1SysId = grAircraft.insert()

	TestCase_GetFuelTypesFromAircraft(strAircraft1SysId,[]);
	TestCase_GetFuelTypesFromAircraft(grAircraft,[]);

	x_snc_devtools.RecordSetValue('x_snc_airline_aircraft_model',strModel1SysId,'fuel_type',strFuelType1SysId);
	TestCase_GetFuelTypesFromAircraft(strAircraft1SysId,[strFuelType1SysId]);
	TestCase_GetFuelTypesFromAircraft(grAircraft,[strFuelType1SysId]);


	x_snc_devtools.RecordSetValue('x_snc_airline_aircraft_model',strModel1SysId,'alternative_fuel_types',strFuelType2SysId);
	TestCase_GetFuelTypesFromAircraft(strAircraft1SysId,[strFuelType1SysId,strFuelType2SysId]);
	TestCase_GetFuelTypesFromAircraft(grAircraft,[strFuelType1SysId,strFuelType2SysId]);

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
