sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetAircraftRecord(vAircraft, vExpected)
	{
		function TestCase_GetAircraftRecord_CheckResult(vInput,vExpected,vResult)
		{
			if (x_snc_devtools.IsValidSysId(vExpected))
			{
				if (x_snc_devtools.IsValidRecord(vResult) == false)
				{
					return false;
				}
				if (vResult.getRecordClassName() != 'x_snc_airline_aircraft')
				{
					return false;
				}
				if (vResult.sys_id != vExpected)
				{
					return false;
				}
				return true;
			}
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function GetAircraftRecord()',
			GetAircraftRecord,
			{
				'vAircraft': vAircraft
			},
			vExpected,
			TestCase_GetAircraftRecord_CheckResult);
	}

	TestCase_GetAircraftRecord(null,false);
	
	var grAircraft = new GlideRecord('x_snc_airline_aircraft');
	grAircraft.initialize();
	var strAircraftSysId = grAircraft.insert();
	
	TestCase_GetAircraftRecord(grAircraft,strAircraftSysId);
	TestCase_GetAircraftRecord(strAircraftSysId,strAircraftSysId);

	var grAircraft = new GlideRecord('x_snc_airline_aircraft');
	grAircraft.initialize();
	grAircraft.tail_number = 'TEST-1';
	var strAircraftSysId = grAircraft.insert();	

	TestCase_GetAircraftRecord('TEST-1',strAircraftSysId);
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
