			vResult = x_snc_devtools.GetArrayValue(vResult); 
			return (vExpected == vResult.length);
		}

		test.TestCase(
			'Function GetPotentialPilotsFromPotentialAirports()',
			GetPotentialPilotsFromPotentialAirports,
