sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetAirportRecord(vAirport, vExpected)
	{
		function TestCase_GetAirportRecord_CheckResult(vInput,vExpected,vResult)
		{
			if (x_snc_devtools.IsValidSysId(vExpected))
			{
				if (x_snc_devtools.IsValidRecord(vResult) == false)
				{
					return false;
				}
				if (vResult.getRecordClassName() != 'x_snc_airline_airport')
				{
					return false;
				}
				if (vResult.sys_id != vExpected)
				{
					return false;
				}
				return true;
			}
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function GetAirportRecord()',
			GetAirportRecord,
			{
				'vAirport': vAirport
			},
			vExpected,
			TestCase_GetAirportRecord_CheckResult);
	}

	TestCase_GetAirportRecord(null,false);
	
	var grAirport = new GlideRecord('x_snc_airline_airport');
	grAirport.initialize();
	grAirport.name = 'TEST1';
	grAirport.icao = 'ZZZZ';
	var strAirportSysId = grAirport.insert();
	
	TestCase_GetAirportRecord(grAirport,strAirportSysId);
	TestCase_GetAirportRecord(strAirportSysId,strAirportSysId);
	TestCase_GetAirportRecord('ZZZZ',strAirportSysId);
	
	TestCase_GetAirportRecord('zzzz',false);
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
