sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_MissionRequiresDeicing(vMission, vExpected)
	{
		function TestCase_MissionRequiresDeicing_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected === false)
			{
				return (vExpected == vResult);
			}
			if (x_snc_devtools.IsArray(vResult) == false)
			{
				return false;
			}
			return vResult.length == vExpected;
		}

		test.TestCase(
			'Function MissionRequiresDeicing()',
			MissionRequiresDeicing,
			{
				'vMission': vMission
			},
			vExpected,
			TestCase_MissionRequiresDeicing_CheckResult);
	}

	TestCase_MissionRequiresDeicing(null,false);

	var grMission = new GlideRecord('x_snc_airline_mission');
	grMission.initialize();
	var strMissionSysId = grMission.insert();
	TestCase_MissionRequiresDeicing(grMission,0);

	var grFlight = new GlideRecord('x_snc_airline_flight');
	grFlight.initialize();
	grFlight.mission = grMission.sys_id;
	grFlight.state = 'planning';
	grFlight.insert();
	TestCase_MissionRequiresDeicing(grMission,1);
	TestCase_MissionRequiresDeicing(grMission,0);

	var grFlight = new GlideRecord('x_snc_airline_flight');
	grFlight.initialize();
	grFlight.mission = grMission.sys_id;
	grFlight.state = 'planning';
	grFlight.insert();
	var grFlight = new GlideRecord('x_snc_airline_flight');
	grFlight.initialize();
	grFlight.mission = grMission.sys_id;
	grFlight.state = 'planning';
	grFlight.insert();	
	TestCase_MissionRequiresDeicing(grMission,2);
	TestCase_MissionRequiresDeicing(grMission,0);	

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
