sys_script_include.script:
function GetAircraftRecord(vAircraft)
{
	var GetRecord = x_snc_devtools.GetRecord;
	var IsValidRecord = x_snc_devtools.IsValidRecord;

	var grAircraft = GetRecord('x_snc_airline_aircraft',vAircraft);

	if (IsValidRecord(grAircraft) == true)
	{
		return grAircraft;
	}

	var grAircraft = new GlideRecord('x_snc_airline_aircraft');
	grAircraft.addQuery('tail_number','=',vAircraft);
	grAircraft.query();
	if (grAircraft.getRowCount() == 1)
	{
		grAircraft.next();
		return grAircraft;
	}
	return false;
}
