sys_script_include.script:
function CreateMissionSheetForFlight(strFlightSysId)
{
	var IsValidRecord = x_snc_devtools.IsValidRecord;

	var grFlight = GetFlightRecord(strFlightSysId);
	if (IsValidRecord(grFlight) == false)
	{
		return false;
	}


	var strHtml = '<html><head></head><body>';
	strHtml += HtmlRenderMissionSheetA4(grFlight); 
	strHtml += '</body></html>';



	var vResult = x_snc_devtools.CreatePDFFromHtml(
		'x_snc_airline_flight',
		grFlight.sys_id,
		grFlight.number+'.pdf',
		strHtml,
		'A4',
		'PORTRAIT',
		0,0,
		false);

	return true;
}
