sys_script_include.script:
function OpenAipApiRequest(strEndPoint, vParams)
{
	var GetValue = x_snc_devtools.GetValue;
	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetCredentialFromAlias = x_snc_devtools.GetCredentialFromAlias;

	var GetKeys = x_snc_devtools.GetKeys;


	function MakeUrlParameters(vParams)
	{
		var strUrlParams = '';
		var aKeys = GetKeys(vParams);
		for (var nKey = 0; nKey < aKeys.length; nKey++)
		{
			var strKey = aKeys[nKey];
			var strValue = GetStringValue(GetValue(vParams,strKey));
			if (strUrlParams != '')
			{
				strUrlParams += '&';
			}
			strUrlParams += strKey + '=' + strValue;
		}
		return strUrlParams;
	}

	var strUrlParams = MakeUrlParameters(vParams);

	var strUrl = 'https://api.core.openaip.net/api/' + strEndPoint + '?' + strUrlParams;

	var credential = GetCredentialFromAlias('x_snc_airline.Open_AIP_API');
	if (credential == false)
	{
		return false;
	}
	var strApiKey = GetStringValue(GetValue(credential,'apikey'));
	if (strApiKey == '')
	{
		return false;
	}

	strUrl += '&apiKey='+strApiKey;

	var requestparams = {};
	requestparams.url = strUrl;
	Debug(requestparams);
	var strResult = x_snc_devtools.HttpRequest(requestparams);
	if (strResult == false)
	{
		return false;
	}
	var result = x_snc_devtools.ParseJson(strResult);
	return result;
}
