sys_script_include.script:
function GetParcelsFromLAGs(aLAGs)
{
	var GetArrayValue = x_snc_devtools.GetArrayValue;
	var ArrayValueExists = x_snc_devtools.ArrayValueExists;
	var GetKeys = x_snc_devtools.GetKeys;
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetIntegerValue = x_snc_devtools.GetIntegerValue;

	aLAGs = GetArrayValue(aLAGs);

	var parcels = {};
	for (var nLAG = 0; nLAG < aLAGs.length; nLAG++)
	{
		var lag = aLAGs[nLAG];
		var aParcelsInLAG = lag.parcels;

		for (var nParcelInLAG = 0; nParcelInLAG < aParcelsInLAG.length; nParcelInLAG++)
		{
			var strParcelInLAGSysId = aParcelsInLAG[nParcelInLAG];

			// The parcel is being loaded (it does not yet exist)
			if (ArrayValueExists(GetKeys(parcels),strParcelInLAGSysId) == false)
			{
				var grParcel = x_snc_parcel.GetParcelRecord(strParcelInLAGSysId);
				if (IsValidRecord(grParcel) == true)
				{
					var parcel = {};
					parcel.sys_id             = strParcelInLAGSysId;
					parcel.number             = GetStringValue(grParcel.number);
					parcel.total_weight       = GetIntegerValue(grParcel.total_weight);
					parcel.categories         = x_snc_parcel.GetCategoriesFromParcels(grParcel.sys_id);
					parcel.departure_icao     = lag.departure_icao;
					parcel.departure_datetime = lag.departure_datetime;
					parcel.arrival_icao     = lag.arrival_icao;
					parcel.arrival_datetime = lag.arrival_datetime;
					parcels[strParcelInLAGSysId] = parcel;
				}

			}
			// The parcel is already on the plane (it exists)
			else
			{
				var parcel = parcels[strParcelInLAGSysId];
				parcel.arrival_icao     = lag.arrival_icao;
				parcel.arrival_datetime = lag.arrival_datetime;
				parcels[strParcelInLAGSysId] = parcel;
			}

		}
	}

	var aParcels = Array();

	var aKeys = GetKeys(parcels);
	for (var nKey = 0; nKey < aKeys.length; nKey++)
	{
		aParcels.push(parcels[aKeys[nKey]]);
	}
	return aParcels;
}
