sys_script_include.script:
function AirportsDataImprovement()
{
	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetFloatValue = x_snc_devtools.GetFloatValue;
	var GetValue = x_snc_devtools.GetValue;

	var grAirports = new GlideRecord('x_snc_airline_airport');
	grAirports.query();
	while (grAirports.next())
	{
		var strICAO = GetStringValue(grAirports.icao);
		var airport = OpenAipApiGetAirportByICAO(strICAO);
		if (airport != false)
		{
			var bUpdate = false;

			var nLongitude = GetFloatValue(GetValue(airport,'geometry','coordinates',0));
			var nLatitude = GetFloatValue(GetValue(airport,'geometry','coordinates',0));

			if (nLongitude != 0 && nLatitude != 0)
			{
				grAirports.longitude = GetValue(airport,'geometry','coordinates',0);
				grAirports.latitude = GetValue(airport,'geometry','coordinates',1);
				bUpdate = true;
			}
			if (bUpdate == true)
			{
				Log('AirportsDataImprovement: Updating airport: '+grAirports.display);
				grAirports.update();
			}
		}
	}
	return true;
}
