sys_script_include.script:
function HtmlRenderMissionSheetA4(grFlight)
{
	var GetStringValue = x_snc_devtools.GetStringValue;
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetRecord = x_snc_devtools.GetRecord;

	var strHtml = '';

	if (IsValidRecord(grFlight) == false)
	{
		strHtml += '<p style="text-color: red;">ERROR!</p>';
		strHtml = HtmlRenderPageA4(strHtml);
		return strHtml;
	}


	strHtml += '<style>';
	strHtml += 'th { background-color: lightgrey; text-align: left; padding: 5px; vertical-align: top; }';
	strHtml += 'td { text-align: left; padding: 5px; vertical-align: top;  }';
	strHtml += '</style>';

	strHtml += '<table style="width: 100%;"><tbody>';

	strHtml += '<tr>';

	strHtml += '<th style="font-size: 200%; text-align: center;" colspan="10">';
	if (grFlight.state == 'planning')
	{
		strHtml += '<span style="color: red;">DRAFT</span> ';
	}
	strHtml += 'Mission Sheet: '+grFlight.number;
	strHtml += '</th>';
	strHtml += '</tr>';

	strHtml += '<tr>';

	strHtml += '<td>';
		strHtml += '<table><tbody>';
		strHtml += '<tr><th>Tail number</th><td>'+grFlight.aircraft.tail_number+'</td></tr>';
		strHtml += '<tr><th>Aircraft model</th><td> '+grFlight.aircraft.model.name+'</td></tr>';
		strHtml += '<tr><th>Pilot</th><td>'+grFlight.pilot.name+'</td></tr>';
		strHtml += '</tbody></table>';
	strHtml += '<td>';

	var aLAGs = GetLAGsFromFlight(grFlight.sys_id);

	strHtml += '<td>';
	strHtml += 'Route:<br/>';
	for (var nLAG = 0; nLAG < aLAGs.length; nLAG++)
	{
		var lag = aLAGs[nLAG];
		strHtml += lag.departure_datetime + ' ' + lag.departure_icao + ' ' + lag.arrival_icao + ' ' + lag.arrival_datetime;
		strHtml += '<br/>';
	}
	strHtml += '</td>';

	strHtml += '</tr>';



	// FLIGHT NUMBER
	//strHtml += '<div style="font-size: '+StringLengthToFontSize(GetStringValue(grParcel.number).length)+';">' + grParcel.number + '</div>';
	//strHtml += '<div style="font-size: 400%;">' + grFlight.number + '</div>';
	//strHtml += '<div style="position: absolute; top: 1cm; font-size: 400%;">' + grParcel.number + '</div>';


	/*
	// TEXT
	var strText = GetStringValue(grParcel.text);
	if (strText != '')
	{
		strHtml += '<div style="font-size: 100%; line-height: 120%;">';
		strHtml += StringReplace(strText,'\n','<br/>')+' ';
		strHtml += '</div>';
	}
	*/

	/*
	// QR CODE
	var aQRCode = x_snc_devtools.CreateQRCodeRawData(GetParcelScanPageURL(grParcel.sys_id));
	strHtml += HtmlRenderBitmap2(aQRCode);
*/

	//strHtml += '<div style="height: 13cm;"/>';


	/*
	// SHIP TO
	var grLocation = GetRecord('cmn_location',grParcel.destination);
	if (IsValidRecord(grLocation))
	{
		strHtml += '<div style="font-size: 350%;"><b>SHIP TO</b></div>'
		strHtml += '<div style="font-size: 200%; line-height: 130%;">';
			strHtml += '<div>'+grLocation.name+'</div>';
			strHtml += '<div>'+StringReplace(grLocation.street,'\n','<br/>')+'</div>';
			strHtml += '<div>'+grLocation.zip+' '+grLocation.city+' '+grLocation.state+' '+grLocation.country+'</div>';
		strHtml += '</div>';

	}
	*/

	/*
	// SHIP FROM
	var grLocationSender = GetRecord('cmn_location',grParcel.sender);
	if (IsValidRecord(grLocationSender))
	{
		strHtml += '<div style="font-size: 100%; margin-top: 10px; border-top: solid; border-width: thick; padding-top: 3px;">';
		strHtml += '<b>SHIP FROM</b> ';
		strHtml += grLocationSender.name+' ';
		strHtml += grLocationSender.street+' ';
		strHtml += grLocationSender.zip+' '+grLocationSender.city+' '+grLocationSender.state+' ';
		strHtml += grLocationSender.country;		
		strHtml += '</div>';
	}
	*/


	/*
	// TASK/CASE
	var grTask = GetRecord('task',grParcel.task);
	if (IsValidRecord(grTask))
	{
		strHtml += '<div style="font-size: 100%; margin-top: 10px; border-top: solid; border-width: thick; padding-top: 3px;">';
		strHtml += '<b>TASK/CASE</b> ';
		strHtml += grTask.number+' ';
		strHtml += '</div>';
	}
	*/

	strHtml += '</tbody></table>';


	strHtml = x_snc_devtools.HtmlRenderPageA4(strHtml,1,1);

	return strHtml;

}
