sys_script_include.script:
function FlightAddStop(vFlight)
{
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetIntegerValue = x_snc_devtools.GetIntegerValue;

	var grFlight = GetFlightRecord(vFlight);
	if (IsValidRecord(grFlight) == false)
	{
		return false;
	}
	var grLastStop = new GlideRecord('x_snc_airline_flight_stop');
	grLastStop.addQuery('flight','=',grFlight.sys_id);
	grLastStop.orderByDesc('stop');
	grLastStop.setLimit(1);
	grLastStop.query();

	var nLastStop = 0;
	if (grLastStop.next() == true)
	{
		nLastStop = GetIntegerValue(grLastStop.stop); 
	}

	var grStop = new GlideRecord('x_snc_airline_flight_stop');
	grStop.initialize();
	grStop.flight = grFlight.sys_id;
	grStop.stop = nLastStop + 1;
	grStop.insert();

	return grStop;
}
