sys_ui_action.script:
var GetStringValue = x_snc_devtools.GetStringValue;

var bError = false;

if (GetStringValue(current.departure) == '')
{
	bError = true;
	gs.addErrorMessage('Departure airdrome has not been set.');
}

if (GetStringValue(current.arrival) == '')
{
	bError = true;
	gs.addErrorMessage('Arrival airdrome has not been set.');
}

if (GetStringValue(current.departure) != '' && (GetStringValue(current.departure) == GetStringValue(current.arrival)))
{
	bError = true;
	gs.addErrorMessage('Departure and arrival airdrome are identical.');
}


if (GetStringValue(current.departure_datetime) == '')
{
	bError = true;
	gs.addErrorMessage('Departure date and time has not been set.');
}

if (GetStringValue(current.arrival_datetime) == '')
{
	bError = true;
	gs.addErrorMessage('Arrival date and time has not been set.');
}

if (GetStringValue(current.departure_datetime) != '' && (GetStringValue(current.departure_datetime) == GetStringValue(current.arrival_datetime)))
{
	bError = true;
	gs.addErrorMessage('Departure and arrival date and time are identical.');
}

if (GetStringValue(current.aircraft) == '')
{
	bError = true;
	gs.addErrorMessage('No aircraft selected.');
}

if (GetStringValue(current.pilot) == '')
{
	bError = true;
	gs.addErrorMessage('No pilot selected.');
}


if (bError == false)
{
	current.state = 'scheduled';
	gs.addInfoMessage('Flight has been scheduled');
}

current.update();
x_snc_devtools.RedirectToRecord(current);
